mapper.select:
select torg.`code`,torg.`fullname`,torg.shortname,torg.logo,torg.brief,torg.description, torg.city,torg.industry,torg.tags,torg.salary,torg.grade,torg.`score`,torg.treatment,torg.`sort`,torg.`ishot`, date_format(torg.createtime,'%Y-%m-%d %H:%i:%s') as createtime, date_format(torg.publishtime,'%Y-%m-%d %H:%i:%s') as publishtime, date_format(torg.shelftime,'%Y-%m-%d %H:%i:%s') as shelftime, date_format(torg.updatetime,'%Y-%m-%d %H:%i:%s') as updatetime, tu.real_name, tc.name as cityname, ti.name as industryname, ifnull((select count(1) from organization_praise where orgcode = torg.code),0) as praiseCount, ifnull((select count(1) from organization_remark o where orgcode = torg.code),0) as commontCount, ifnull((select count(1) from organization_collection where orgcode = torg.code),0) as collectCount from organization torg left join system_user tu on torg.creater = tu.id left join city tc on torg.city = tc.id left join industry ti on torg.industry = ti.id where 1=1
and (torg.fullname like concat('%',#{name},'%') or torg.shortname like concat('%',#{name},'%') )
and torg.industry = #{industry}
and torg.city = #{city}
and torg.ishot = #{ishot}
order by torg.ishot desc,torg.sort
limit ${offset},${limit}

select
count(1)
from organization torg
left join system_user tu on torg.creater = tu.id
where 1=1

    and (torg.fullname like concat('%',#{name},'%') or torg.shortname like concat('%',#{name},'%') )


    and torg.industry = #{industry}


    and torg.city = #{city}


    and torg.ishot = #{ishot}

select torg.`code`,torg.`fullname`,torg.shortname,torg.logo,torg.brief,torg.description, torg.city,torg.industry,torg.tags,torg.salary,torg.grade,torg.`score`,torg.`sort`, date_format(torg.createtime,'%Y-%m-%d %H:%i:%s') as createtime, date_format(torg.publishtime,'%Y-%m-%d %H:%i:%s') as publishtime, date_format(torg.shelftime,'%Y-%m-%d %H:%i:%s') as shelftime, date_format(torg.updatetime,'%Y-%m-%d %H:%i:%s') as updatetime, tu.real_name, tc.name as cityname, ti.name as industryname from organization torg left join system_user tu on torg.creater = tu.id left join city tc on torg.city = tc.id left join industry ti on torg.industry = ti.id where 1=1
and fullname = #{fullname}
and shortname = #{shortname}

select o.id, o.memberid, o.orgcode, o.orgname, o.city, o.workyear, o.post_status, o.salary_pre_tax, o.other_benefit, o.year_end_money, o.growth, o.workload, o.cultural_context, o.satisfaction, o.theme, date_format(o.createtime,'%Y-%m-%d %H:%i:%s') as createtime, o2.id, o2.fullname, o2.shortname,c.name,m.nickname,m.username from organization_remark o left join organization o2 on o.orgcode = o2.code left join city c on c.id = o.city left join `member` m on m.id = o.memberid where 1=1
and (o2.fullname like concat('%',#{name},'%') or o2.shortname like concat('%',#{name},'%') )
and o.orgcode = #{orgcode}
and o.city = #{city}
limit ${offset},${limit}

select count(1)
from organization_remark o left join organization o2 on o.orgcode = o2.code
where 1=1

    and (o2.fullname like concat('%',#{name},'%') or o2.shortname like concat('%',#{name},'%') )


    and o.orgcode = #{orgcode}


    and o.city = #{city}

select id,`name`,ishot,`status`,sort,date_format(createtime,'%Y-%m-%d %H:%i:%s') as createtime from city where `status` = 'ENABLE'
and `name` like concat('%',#{name},'%')
and `ishot` = #{ishot}
order by ishot desc,sort limit ${offset},${limit}

select count(1) from city where `status` = 'ENABLE'
and `name` like concat('%',#{name},'%')
and `ishot` = #{ishot}

mapper.update:
update city
set `name` = #{name},
ishot  = #{ishot},
sort   = ${sort}
where id = #{id}

mapper.select:
