select count(1)
from organization torg
left join system_user tu on torg.creater = tu.id
left join city tc on torg.city = tc.id
left join industry ti on torg.industry = ti.id
where 1=1

    and (torg.fullname like concat('%',#{name},'%') or torg.shortname like concat('%',#{name},'%'))


    and torg.industry = #{industry}


    and torg.city = #{city}


    and torg.ishot = #{ishot}

select torg.`code`, torg.`fullname`, torg.shortname, torg.logo, torg.brief, torg.description, torg.city, torg.industry, torg.tags, torg.salary, torg.grade, torg.`score`, torg.`sort`, torg.`ishot`, date_format(torg.createtime, '%Y-%m-%d %H:%i:%s') as createtime, date_format(torg.publishtime, '%Y-%m-%d %H:%i:%s') as publishtime, date_format(torg.shelftime, '%Y-%m-%d %H:%i:%s') as shelftime, date_format(torg.updatetime, '%Y-%m-%d %H:%i:%s') as updatetime, tu.real_name, tc.name as cityname, ti.name as industryname, (select count(1) from organization_praise where orgcode = torg.code) as praise_count, (select count(1) from organization_remark o where orgcode = torg.code) as commont_count, (select count(1) from organization_collection where orgcode = torg.code) as collect_count from organization torg left join system_user tu on torg.creater = tu.id left join city tc on torg.city = tc.id left join industry ti on torg.industry = ti.id where 1 = 1
and (torg.fullname like concat('%',#{name},'%') or torg.shortname like concat('%',#{name},'%'))
and torg.industry = #{industry}
and torg.city = #{city}
and torg.ishot = #{ishot}
order by praise_count desc limit 0,5;

select id, orgcode, memberid, date_format(createtime, '%Y-%m-%d %H:%i:%s') as createtime
from organization_praise
where orgcode = #{code}
and memberid = #{memberid}

mapper.delete:
delete
from organization_praise
where orgcode = #{code}
and memberid = #{memberid}

