mapper.select:
select tp.page_view,tp.workplace,tp.job_salary,
,
(SELECT COUNT(1) FROM save_position_member spm WHERE tp.code = spm.position_code) AS posSaveCount
from `position` tp join organization tor on tp.orgcode = tor.code
left join city tc on tp.address = tc.id
left join industry ti on tor.industry = ti.id
left join system_user tu on tu.id = tp.creater
where 1=1

    and tp.title like concat("%",#{title},"%")


    and tp.orgcode = #{orgcode}


    and tp.type = #{type}


    and tor.industry = #{industry}


    and tp.address = #{city}


    and tp.ishot = #{ishot}


    and tp.isstar = #{isstar}

order by ishot desc,publishtime desc,createtime desc

    limit ${offset},${limit}

select count(1)
from position tp join organization tor on tp.orgcode = tor.code
where 1=1

    and tp.title like concat("%",#{title},"%")


    and tp.orgcode = #{orgcode}


    and tp.type = #{type}


    and tor.industry = #{industry}


    and tor.city = #{city}


    and tp.ishot = #{ishot}


    and tp.isstar = #{isstar}

select title
from position
where title = #{sc}

select keywords
from hot_position
where id = #{id}

select
from `position` tp join organization tor on tp.orgcode = tor.code left join city tc on tp.address = tc.id left join industry ti on tor.industry = ti.id left join system_user tu on tu.id = tp.creater where now() > tp.publishtime and tp.shelftime >now()
and tp.type = #{type}
and tor.industry = #{industry}
and tp.address = #{city}
and ( instr(tp.title,#{searchContent}) > 0 or instr(tor.fullname,#{searchContent}) > 0 or instr(tor.shortname,#{searchContent}) > 0
or instr(tp.title,#{word}) > 0 or instr(tor.fullname,#{word}) > 0 or instr(tor.shortname,#{word}) > 0
)
order by ishot desc,publishtime desc,createtime desc limit ${pageStart},${pageSize}

select count(1) from `position` tp join organization tor on tp.orgcode = tor.code left join city tc on tp.address = tc.id left join industry ti on tor.industry = ti.id left join system_user tu on tu.id = tp.creater where now() > tp.publishtime and tp.shelftime >now()
and tp.type = #{type}
and tor.industry = #{industry}
and tp.address = #{city}
and ( instr(tp.title,#{searchContent}) > 0 or instr(tor.fullname,#{searchContent}) > 0 or instr(tor.shortname,#{searchContent}) > 0
or instr(tp.title,#{word}) > 0 or instr(tor.fullname,#{word}) > 0 or instr(tor.shortname,#{word}) > 0
)
order by tp.ishot desc,tp.publishtime desc,tp.createtime desc

select , (SELECT COUNT(1) FROM save_position_member spm WHERE tp.code = spm.position_code) AS hotCount from `position` tp join organization tor on tp.orgcode = tor.code left join city tc on tp.address = tc.id left join industry ti on tor.industry = ti.id left join system_user tu on tu.id = tp.creater where now() > tp.publishtime and tp.shelftime >now()
and tp.type = #{type}
and tor.industry = #{industry}
and tor.city = #{city}
and ( instr(tp.title,#{searchContent}) > 0 or instr(tor.fullname,#{searchContent}) > 0 or instr(tor.shortname,#{searchContent}) > 0
or instr(tp.title,#{word}) > 0 or instr(tor.fullname,#{word}) > 0 or instr(tor.shortname,#{word}) > 0
)
order by hotCount desc limit ${pageStart},${pageSize}

select count(1) from `position` tp join organization tor on tp.orgcode = tor.code left join city tc on tp.address = tc.id left join industry ti on tor.industry = ti.id left join system_user tu on tu.id = tp.creater where now() > tp.publishtime and tp.shelftime >now()
and tp.type = #{type}
